import { useContext, useEffect, useState } from "react";
import { AuthContext } from "./AuthContext";

const HomeUploads = () => {
  const { user } = useContext(AuthContext);
  const [items, setItems] = useState([]);

  useEffect(() => {
    setItems(JSON.parse(localStorage.getItem("uploads")) || []);
  }, []);

  const del = (id) => {
    const updated = items.filter(i => i.id !== id);
    setItems(updated);
    localStorage.setItem("uploads", JSON.stringify(updated));
  };

  return (
    <>
      {items.map(i => (
        <div key={i.id}>
          {i.type === "photo" ? <img src={i.file} width="200"/> :
            <video src={i.file} width="300" controls />}
          <p>{i.desc}</p>
          {user && <button onClick={()=>del(i.id)}>Delete</button>}
        </div>
      ))}
    </>
  );
};
