import { useContext, useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import { LanguageContext } from "../components/languageContext";
import { AuthContext } from "./AuthContext";

export default function Dhamadeshana() {
  const { lang } = useContext(LanguageContext);
  const { user } = useContext(AuthContext);

  const [textEN, setTextEN] = useState("");
  const [textMR, setTextMR] = useState("");

  useEffect(() => {
    const savedEN = localStorage.getItem("dhammaEN");
    const savedMR = localStorage.getItem("dhammaMR");

    if (savedEN) setTextEN(savedEN);
    if (savedMR) setTextMR(savedMR);
  }, []);

  const save = () => {
    localStorage.setItem("dhammaEN", textEN);
    localStorage.setItem("dhammaMR", textMR);
    alert("Dhamadeshana updated");
  };

  return (
    <Container style={{ marginLeft: "10%" }}>
      <div className="leaflet">

        {/* EDIT MODE – ONLY LOGGED USERS */}
        {user && (
          <>
            <h4>Edit Dhamadeshana</h4>

            <textarea
              placeholder="English Dhamadeshana"
              value={textEN}
              onChange={(e) => setTextEN(e.target.value)}
              rows={6}
              style={{ width: "100%", marginBottom: "10px" }}
            />

            <textarea
              placeholder="Marathi Dhamadeshana"
              value={textMR}
              onChange={(e) => setTextMR(e.target.value)}
              rows={6}
              style={{ width: "100%", marginBottom: "10px" }}
            />

            <button onClick={save}>Save</button>
            <hr />
          </>
        )}

        {/* DISPLAY MODE – PUBLIC */}
        {lang === "EN" ? (
          <>
            <h2>Dhamadeshana</h2>
            <p>
              {textEN ||
              <p>
               <h2>Dhamadeshana</h2>

          <p>
            Dhamadeshana means the teaching and explanation of Buddha Dhamma.
            These teachings guide human life towards wisdom, morality, and
            mental discipline.
          </p>

          <p>
            The Buddha taught the Noble Eightfold Path as the way to overcome
            suffering and attain peace. Dhamadeshana encourages people to live
            a life based on right understanding, right conduct, and compassion.
          </p>

          <p>
            Through Dhamadeshana, individuals learn the values of equality,
            non-violence, mindfulness, and social responsibility as emphasized
            by Dr. B. R. Ambedkar.
          </p>

          <ul>
            <li>Right View and Right Intention</li>
            <li>Right Speech and Right Action</li>
            <li>Right Livelihood</li>
            <li>Right Effort, Mindfulness, and Concentration</li>
          </ul>
          </p>
          }
            </p>
          </>
        ) : (
          <>
            <h2>धम्मदेशना</h2>
            <p>
              {textMR ||

              <p>
               <h2>धम्मदेशना</h2>

          <p>
            धम्मदेशना म्हणजे बुद्ध धम्माचे उपदेश व त्याचे स्पष्टीकरण.
            हे उपदेश मानवाला प्रज्ञा, शील आणि समाधीच्या मार्गावर
