import { useContext, useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import { LanguageContext } from "../components/languageContext";
import { AuthContext } from "./AuthContext";

export default function Dhamadeshana() {
  const { lang } = useContext(LanguageContext);
  const { user } = useContext(AuthContext);

  const [uploads, setUploads] = useState([]);

  // LOAD UPLOADS
  useEffect(() => {
    const data =
      JSON.parse(localStorage.getItem("dhamadeshanaUploads")) || [];
    setUploads(data);
  }, []);

  // DELETE UPLOAD (ONLY LOGGED USER)
  const deleteUpload = (id) => {
    const filtered = uploads.filter((item) => item.id !== id);
    setUploads(filtered);
    localStorage.setItem(
      "dhamadeshanaUploads",
      JSON.stringify(filtered)
    );
  };

  return (
    <Container style={{ marginLeft: "10%" }}>
      {/* CONTENT */}
      <div className="leaflet">
        {lang === "EN" ? (
          <>
            <h2>Dhamadeshana</h2>

            <p>
              Dhamadeshana means the teaching and explanation of Buddha
              Dhamma. These teachings guide human life towards wisdom,
              morality, and mental discipline.
            </p>

            <p>
              The Buddha taught the Noble Eightfold Path as the way to
              overcome suffering and attain peace.
            </p>

            <p>
              Through Dhamadeshana, people learn equality, non-violence,
              mindfulness, and social responsibility as emphasized by
              Dr. B. R. Ambedkar.
            </p>
          </>
        ) : (
          <>
            <h2>धम्मदेशना</h2>

            <p>
              धम्मदेशना म्हणजे बुद्ध धम्माचे उपदेश व त्याचे स्पष्टीकरण.
              हे उपदेश मानवाला प्रज्ञा, शील आणि समाधीच्या मार्गावर
              नेण्यासाठी मार्गदर्शक आहेत.
            </p>

            <p>
              दुःखाच्या निरोधासाठी आणि शांततेसाठी बुद्धांनी
              अष्टांगिक मार्ग सांगितला.
            </p>

            <p>
              समता, अहिंसा, सजगता आणि सामाजिक जबाबदारीचे महत्त्व
              डॉ. बाबासाहेब आंबेडकरांनी अधोरेखित केले आहे.
            </p>
          </>
        )}
      </div>

      {/* UPLOADED PHOTOS & VIDEOS */}
      <hr />
      <h3>Photos & Videos</h3>

      {uploads.length === 0 && <p>No uploads yet</p>}

      {uploads.map((item) => (
        <div key={item.id} style={{ marginBottom: "20px" }}>
          {item.type === "photo" ? (
            <img src={item.url} alt="" width="250" />
          ) : (
            <video src={item.url} controls width="350" />
          )}

          <p>{item.desc}</p>

          {/* DELETE OPTION ONLY FOR LOGGED USERS */}
          {user && (
            <button onClick={() => deleteUpload(item.id)}>
              Delete
            </button>
          )}
        </div>
      ))}
    </Container>
  );
}
