import { useContext, useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import { LanguageContext } from "../components/languageContext";
import { AuthContext } from "./AuthContext";

export default function Philosophy() {
  const { lang } = useContext(LanguageContext);
  const { user } = useContext(AuthContext);

  const [textEN, setTextEN] = useState("");
  const [textMR, setTextMR] = useState("");

  // DEFAULT CONTENT
  const DEFAULT_EN = (
    <>
      <p>Dr. Ambedkar's philosophy emphasizes equality, justice, and human dignity.</p>
      <p><strong>Social Equality:</strong> He opposed caste discrimination and untouchability.</p>
      <p><strong>Social Justice:</strong> Justice through law, rights, and reform.</p>
      <p><strong>Education:</strong> Education as the strongest tool for empowerment.</p>
      <p><strong>Liberty, Equality, Fraternity:</strong> Foundation of a just society.</p>
    </>
  );

  const DEFAULT_MR = (
    <>
      <p>डॉ. आंबेडकरांचे तत्त्वज्ञान समता, न्याय आणि मानवतेवर आधारित आहे.</p>
      <p><strong>सामाजिक समता:</strong> जातिभेद व अस्पृश्यतेला विरोध.</p>
      <p><strong>सामाजिक न्याय:</strong> कायदे व हक्कांद्वारे न्याय.</p>
      <p><strong>शिक्षण:</strong> सक्षमीकरणाचे प्रभावी साधन.</p>
      <p><strong>स्वातंत्र्य, समता, बंधुता:</strong> समाजाची पायाभरणी.</p>
    </>
  );

  // Load saved data
  useEffect(() => {
    setTextEN(localStorage.getItem("philosophyEN") || "");
    setTextMR(localStorage.getItem("philosophyMR") || "");
  }, []);

  const save = () => {
    localStorage.setItem("philosophyEN", textEN);
    localStorage.setItem("philosophyMR", textMR);
    alert("Ambedkar Philosophy saved");
  };

  const remove = () => {
    localStorage.removeItem("philosophyEN");
    localStorage.removeItem("philosophyMR");
    setTextEN("");
    setTextMR("");
    alert("Ambedkar Philosophy reset to default");
  };

  return (
    <Container style={{ marginLeft: "10%" }}>
      <div className="leaflet">

        {/* EDIT MODE */}
        {user && (
          <>
            <h4>Edit Ambedkar Philosophy</h4>

            <textarea
              value={textEN}
              onChange={(e) => setTextEN(e.target.value)}
              placeholder="English Philosophy"
              rows={6}
              style={{ width: "100%", marginBottom: "10px" }}
            />

            <textarea
              value={textMR}
              onChange={(e) => setTextMR(e.target.value)}
              placeholder="Marathi Philosophy"
              rows={6}
              style={{ width: "100%", marginBottom: "10px" }}
            />

            <button onClick={save}>Save</button>
            <button onClick={remove} style={{ marginLeft: "10px" }}>
              Reset
            </button>

            <hr />
          </>
        )}

        {/* DISPLAY MODE */}
        {lang === "EN"
          ? textEN ? <p>{textEN}</p> : DEFAULT_EN
          : textMR ? <p>{textMR}</p> : DEFAULT_MR}
      </div>
    </Container>
  );
}
